queries.query:
from(bucket: "nocblecpu")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) =>
      r._measurement == "pi_file_count"
  )
  |> aggregateWindow(every: v.windowPeriod, fn: last, createEmpty: false)
  |> difference(columns: ["_value"])
  |> map(fn: (r) => ({
      _time: r._time,
      _value: if float(v: r._value) < 0.0 then 0.0 else float(v: r._value),
      _field: "file_counter"
  }))
  |> yield(name: "file_counter")
